// Preparar jobs de sincronização para cada fonte
const sources = $input.all().map(i => i.json);

return sources.map(source => ({
  sourceId: source.id,
  platform: source.platform,
  creatorName: source.creator_name,
  lastChecked: source.last_checked_at,
  checkFrequency: source.check_frequency_hours
}));

