INSERT INTO activity_log (action, entity_type, details)
VALUES (
  'monitor_run',
  'system',
  '{{ JSON.stringify({
    sources_queued: $('Prepare Sync Jobs').all().length,
    stats: $('Get System Stats').first().json
  }) }}'::jsonb
);
